package fr.cg95.cvq.business.external;

import java.io.Serializable;

/**
 * @author bor@zenexity.fr
 */
public class ExternalServiceIndividualMapping implements Serializable {

    private static final long serialVersionUID = 1L;

    /**
     * The internal CapDemat identifier.
     */
    private Long individualId;
    
    /**
     * An unique external identifier generated by CapDemat and transmitted to the 
     * external service.
     */
    private String externalCapDematId;
    
    /**
     * The identifier known to the external service.
     */
    private String externalId;

    /**
     * @hibernate.property
     *  column="individual_id"
     */
    public Long getIndividualId() {
        return individualId;
    }

    public void setIndividualId(Long individualId) {
        this.individualId = individualId;
    }

    /**
     * @hibernate.property
     *  column="external_capdemat_id"
     */
    public String getExternalCapDematId() {
        return externalCapDematId;
    }

    public void setExternalCapDematId(String externalCapDematId) {
        this.externalCapDematId = externalCapDematId;
    }

    /**
     * @hibernate.property
     *  column="external_id"
     */
    public String getExternalId() {
        return externalId;
    }

    public void setExternalId(String externalId) {
        this.externalId = externalId;
    }

    @Override
    public int hashCode() {
        final int prime = 31;
        int result = 1;
        result = prime * result + ((individualId == null) ? 0 : individualId.hashCode());
        return result;
    }

    @Override
    public boolean equals(Object obj) {
        if (this == obj)
            return true;
        if (obj == null)
            return false;
        if (getClass() != obj.getClass())
            return false;
        ExternalServiceIndividualMapping other = (ExternalServiceIndividualMapping) obj;
        if (individualId == null) {
            if (other.individualId != null)
                return false;
        } else if (!individualId.equals(other.individualId))
            return false;
        return true;
    }
}
